import * as lib from "jsr:@space-operator/flow-lib";
import * as web3 from "npm:@solana/web3.js";
import { Instructions } from "jsr:@space-operator/flow-lib/context";

interface Inputs {
  from: web3.PublicKey;
  to: web3.PublicKey;
  amount: number;
}

export default class Playground extends lib.BaseCommand {
  async run(
    ctx: lib.Context,
    params: Inputs,
  ): Promise<Record<string, any>> {
    const result = await ctx.execute(
      new Instructions(
        params.from,
        [params.from],
        [
          web3.SystemProgram.transfer({
            fromPubkey: params.from,
            toPubkey: params.to,
            lamports: params.amount,
          }),
        ]
      ),
      {}
    );

    return {
      signature: result.signature!,
    };
  }
}
